import React, { useState } from "react";
import {
  Menu, Home, ShoppingCart, RotateCw, BarChart2, DollarSign,
  Briefcase, Package, Calendar, AlertTriangle, Info,
  Users, HelpCircle, X
} from "lucide-react";
import { Link, useLocation } from "react-router-dom";

const menu = [
  { label: "Dashboard", icon: Home, path: "/" },
  { label: "Orders", icon: ShoppingCart, path: "/orders" },
  { label: "Returns", icon: RotateCw, path: "/returns" },
  { label: "Reports", icon: BarChart2, path: "/reports" },
  { label: "Payouts & Account", icon: DollarSign, path: "/payouts" },
  { label: "Services", icon: Briefcase, path: "/services" },
  {
    label: "Items", icon: Package, sub: [
      { name: "All Items", path: "/items/all" },
      { name: "Attributes & Values", path: "/items/attributes" }
    ]
  },
  {
    label: "Planning", icon: Calendar, sub: [
      { name: "Promotions", path: "/planning/promotions" },
      { name: "Calendar", path: "/planning/calendar" }
    ]
  },
  {
    label: "Performance", icon: AlertTriangle, sub: [
      { name: "Order Defect Rate", path: "/performance/defects" },
      { name: "Sales Metrics", path: "/performance/sales" }
    ]
  },
  { label: "Business Info", icon: Info, path: "/business" },
  { label: "Partner Settings", icon: Users, path: "/settings" },
  { label: "Support", icon: HelpCircle, path: "/support" },
];

const Sidebar = () => {
  const [openMenus, setOpenMenus] = useState({});
  const [openMobile, setOpenMobile] = useState(false);
  const location = useLocation();

  const toggleMenu = (label) => {
    setOpenMenus((prev) => ({ ...prev, [label]: !prev[label] }));
  };

  const isActive = (path) => location.pathname === path;

  const SidebarContent = () => (
    <div className="w-64 h-full bg-gray-900 text-white z-50 fixed md:static overflow-y-auto">
      <div className="flex justify-between items-center px-4 py-4 bg-gray-800 font-bold text-lg border-b border-gray-700">
        <span>Target +</span>
        <button onClick={() => setOpenMobile(false)} className="md:hidden">
          <X />
        </button>
      </div>

      <nav className="py-2 space-y-1 text-sm">
        {menu.map((item, idx) => (
          <div key={idx}>
            <div
              className={`px-4 py-2 cursor-pointer flex justify-between items-center hover:bg-gray-700 ${
                isActive(item.path) ? "bg-gray-700 font-semibold" : ""
              }`}
              onClick={() => item.sub ? toggleMenu(item.label) : setOpenMobile(false)}
            >
              <Link to={item.path || "#"} className="flex items-center gap-2">
                <item.icon className="w-4 h-4" />
                <span>{item.label}</span>
              </Link>
              {item.sub && <span className="text-xs">{openMenus[item.label] ? "▲" : "▼"}</span>}
            </div>

            {item.sub && openMenus[item.label] && (
              <div className="pl-10 py-1 space-y-1 text-gray-300">
                {item.sub.map((sub, subIdx) => (
                  <Link
                    key={subIdx}
                    to={sub.path}
                    className={`block hover:text-white ${
                      isActive(sub.path) ? "text-white font-semibold" : ""
                    }`}
                    onClick={() => setOpenMobile(false)}
                  >
                    {sub.name}
                  </Link>
                ))}
              </div>
            )}
          </div>
        ))}
      </nav>
    </div>
  );

  return (
    <>
      <button
        className="md:hidden p-2 fixed top-3 left-3 z-50 bg-white rounded shadow"
        onClick={() => setOpenMobile(true)}
      >
        <Menu className="text-black" />
      </button>

      <div className="hidden md:block">
        <SidebarContent />
      </div>

      {openMobile && (
        <div className="fixed inset-0 z-40 bg-black bg-opacity-50" onClick={() => setOpenMobile(false)}>
          <div onClick={(e) => e.stopPropagation()}>
            <SidebarContent />
          </div>
        </div>
      )}
    </>
  );
};

export default Sidebar;
